// components/AdDetails.jsx
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './styles/adDetails.css';

const AdDetails = () => {
  const { adId } = useParams();
  const [ad, setAd] = useState(null);

  useEffect(() => {
    const fetchAd = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/get_ad/${adId}`);
        setAd(response.data);
      } catch (error) {
        console.error('Error fetching ad details:', error);
      }
    };

    fetchAd();
  }, [adId]);

  if (!ad) {
    return <div>Loading...</div>;
  }

  return (
    <div className="ad-details-container">
      <div className="left-section">
        {ad.photo && <img src={`data:image/jpeg;base64,${ad.photo}`} alt={ad.title} />}
        <button className="bid-button">Сделать ставку</button>
      </div>
      <div className="right-section">
        <h2>{ad.title}</h2>
        <p><strong>Город:</strong> {ad.city}</p>
        <p><strong>Описание:</strong> {ad.description}</p>
        <p><strong>Начальная цена:</strong> {ad.start_price}</p>
        <p><strong>Категория:</strong> {ad.category}</p>
        <p><strong>Пользователь:</strong> {ad.user_name}</p>
      </div>
    </div>
  );
};

export default AdDetails;
