import React, { useState, useEffect, useRef } from 'react';
import './styles/create.css'; // Подключаем CSS файл

const CreateAd = () => {
  const [formData, setFormData] = useState({
    title: '',
    category: '',
    start_price: '',
    city: '',
    description: '',
    photo: null,
  });

  const photoInputRef = useRef(null);
  const uploadBoxRef = useRef(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: name === 'start_price' ? parseInt(value, 10) : value,
    });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    console.log('Selected file:', file);

    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        if (uploadBoxRef.current) {
          uploadBoxRef.current.innerHTML = ''; // Clear the upload box
          const img = document.createElement('img');
          img.src = e.target.result;
          img.style.objectFit = 'cover'; // Ensure the image covers the container
          uploadBoxRef.current.appendChild(img);
        }
      };
      reader.readAsDataURL(file);
    }

    setFormData({
      ...formData,
      photo: file,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.photo) {
      alert('Please upload a photo.');
      return;
    }
  
    const form = new FormData();
    for (const key in formData) {
      form.append(key, formData[key]);
    }
    form.append('user_id', 1); // Здесь можно подставить ID текущего пользователя
  
    // Добавим console.log для проверки данных
    for (const pair of form.entries()) {
        console.log(`${pair[0]}: ${pair[1]}`);
    }
  
    try {
      const response = await fetch('http://localhost:8000/createAd', {
        method: 'POST',
        body: form,
        mode: 'no-cors', // добавлено
        headers: {
          'Accept': 'application/json',
        },
      });
  
      console.log('Ответ сервера:', response);
  
      if (response.ok) {
        alert('Объявление успешно создано!');
      } else {
        console.error('Ошибка сервера:', response); // Логирование ошибки сервера
        alert('Ошибка при создании объявления.');
      }
    } catch (error) {
      console.error('Ошибка:', error);
      alert('Ошибка при создании объявления.');
    }
  };
  

  useEffect(() => {
    const handleUploadBoxClick = () => {
      if (photoInputRef.current) {
        photoInputRef.current.click();
      }
    };

    const uploadBox = uploadBoxRef.current;
    if (uploadBox) {
      uploadBox.addEventListener('click', handleUploadBoxClick);
    }

    return () => {
      if (uploadBox) {
        uploadBox.removeEventListener('click', handleUploadBoxClick);
      }
    };
  }, []);

  return (
    <form className="create-message" onSubmit={handleSubmit}>
      <div className="form-left">
        <div className="form-group">
          <div className="image-upload">
            <label htmlFor="photo">
              <div className="upload-box" ref={uploadBoxRef}>
                <span className="upload-icon">+</span>
              </div>
            </label>
            <input
              type="file"
              id="photo"
              name="photo"
              accept="image/*"
              style={{ display: 'none' }}
              onChange={handleFileChange}
              ref={photoInputRef}
            />
          </div>
        </div>
      </div>

      <div className="form-right">
        <div className="form-group">
          <label htmlFor="title">Название:</label>
          <input
            type="text"
            id="title"
            name="title"
            size="30"
            value={formData.title}
            onChange={handleChange}
          />
        </div>

        <div className="form-group">
          <label htmlFor="category">Категория:</label>
          <select
            id="category"
            name="category"
            value={formData.category}
            onChange={handleChange}
          >
            <option value="test1">Тест1</option>
            <option value="test2">Тест2</option>
            <option value="test3">Тест3</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="start_price">Число:</label>
          <input
            type="number"
            id="start_price"
            name="start_price"
            value={formData.start_price}
            onChange={handleChange}
          />
        </div>

        <div className="form-group">
          <label htmlFor="city">Город:</label>
          <select
            id="city"
            name="city"
            value={formData.city}
            onChange={handleChange}
          >
            <option value="test1">Тест1</option>
            <option value="test2">Тест2</option>
            <option value="test3">Тест3</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="description">Описание:</label>
          <textarea
            id="description"
            name="description"
            rows="10"
            cols="50"
            value={formData.description}
            onChange={handleChange}
          ></textarea>
        </div>

        <input type="submit" value="Опубликовать" />
      </div>
    </form>
  );
};

export default CreateAd;
