// components/AdDetail.jsx
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './styles/adDetail.css';

const AdDetail = () => {
  const { ad_id } = useParams();
  const [ad, setAd] = useState(null);

  useEffect(() => {
    const fetchAd = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/get_ad/${ad_id}`);
        setAd(response.data);
      } catch (error) {
        console.error('Error fetching ad details:', error);
      }
    };

    fetchAd();
  }, [ad_id]);

  if (!ad) {
    return <div>Loading...</div>;
  }

  return (
    <div className="ad-detail">
      <div className="left-section">
        <img src={`data:image/jpeg;base64,${ad.photo}`} alt={ad.title} />
        <button>Сделать ставку</button>
      </div>
      <div className="right-section">
        <h2>{ad.title}</h2>
        <p><strong>City:</strong> {ad.city}</p>
        <p><strong>Description:</strong> {ad.description}</p>
        <p><strong>Start Price:</strong> {ad.start_price}</p>
        <p><strong>Category:</strong> {ad.category}</p>
        <p><strong>User:</strong> {ad.user_name}</p>
      </div>
    </div>
  );
};

export default AdDetail;
