import React from 'react';
import { useHistory } from 'react-router-dom'; // Импортируем useHistory из react-router-dom
import './styles/index.css'; // Импортируем стили
import logo from './images/logo.png'; // Импортируем изображения
import SVG from './images/SVG.png';

const Home = () => {
  const history = useHistory(); // Инициализируем useHistory

  // Функция для обработки нажатия на кнопку "Start Now"
  const handleStartNow = () => {
    history.push('/register'); // Перенаправляем пользователя на страницу /register
  };

  return (
    <div className="container">
      <div className="heading">
        <div className="logo">
          <div className="icon">
            <img src={logo} alt="logo" className="icon" />
          </div>
          <div className="text">
            <p className="text-logo">oadstar</p>
          </div>
        </div>
      </div>
      <div className="svg-container">
        <img src={SVG} alt="SVG Image" className="svg-image" />
      </div>
      <div className="sidebar">
        <p className="sidebar-text">Roadstar</p>
        <div className="additional-text">
          <div>Buy Sell things easier</div>
          <div>along with our service</div>
        </div>
        <div className="register-text">
          <div>Register and start selling your items with the comfort of putting them up for auction.</div>
          <div>We will help you with this.</div>
        </div>
        {/* Добавляем обработчик нажатия на кнопку "Start Now" */}
        <button className="start-button" onClick={handleStartNow}>Start Now</button>
        <button className="more-button">More</button>
      </div>
    </div>
  );
};

export default Home;
