import React, { useContext, useEffect, useState } from 'react';
import './styles/main.css';
import logoR from './images/logoR.png';
import iconLoop from './images/iconLoop.png';
import Avatar from './images/Avatar.png';
import icon1 from './images/icon1.png';
import icon2 from './images/icon2.png';
import icon3 from './images/icon3.png';
import icon4 from './images/icon4.png';
import icon5 from './images/icon5.png';
import icon6 from './images/icon6.png';
import icon7 from './images/icon7.png';
import axios from 'axios';
import { UserContext } from '../UserContext';

const MainPage = () => {
  const { user } = useContext(UserContext);
  const [ads, setAds] = useState([]);
  const [connectedToServer, setConnectedToServer] = useState(false);
  const [dataLoading, setDataLoading] = useState(true); // Добавляем состояние для отслеживания загрузки данных

  const handleSearch = (e) => {
    console.log('Search:', e.target.value);
  };

  useEffect(() => {
    const fetchAds = async () => {
      try {
        const response = await axios.get('http://localhost:8000/get_ad');
        setAds(response.data);
        setDataLoading(false); // Устанавливаем флаг загрузки данных в false после получения данных
      } catch (error) {
        console.error('Error fetching ads:', error);
      }
    };

    const checkServerConnection = async () => {
      try {
        await axios.get('http://localhost:8000');
        setConnectedToServer(true);
      } catch (error) {
        console.error('Error connecting to server:', error);
      }
    };

    checkServerConnection();
    fetchAds();
  }, []);

  return (
    <div>
      <div className="header">
        <img src={logoR} alt="Logo" className="logo" />

        <div className="hat">
          <div className="search-box">
            <input type="text" className="search-input" placeholder="Search..." onChange={handleSearch} />
            <img src={iconLoop} alt="Loop" className="search-icon" />
          </div>

          <div className="roadstar">Roadstar</div>

          <div className="user-info">
            <span className="username">{user ? user.name : 'Александр'}</span>
            <img src={Avatar} alt="Avatar" className="avatar" />
          </div>
        </div>
      </div>

      <div className="left-nav-block">
        <ul className="nav-list">
          <li><img src={icon1} alt="icon1" /> Мои объявления</li>
          <li><img src={icon2} alt="icon2" /> Сообщения</li>
          <li><img src={icon3} alt="icon3" /> Поддержка</li>
          <li><img src={icon4} alt="icon4" /> Мои ставки</li>
          <li><img src={icon5} alt="icon5" /> Контакты</li>
          <li><img src={icon6} alt="icon6" /> Создать объявление</li>
          <li><img src={icon7} alt="icon7" /> Настройки</li>
        </ul>
      </div>

      {connectedToServer ? (
        dataLoading ? (
          <div>Loading...</div>
        ) : (
          <div className="ads-container">
            {ads.map(ad => (
              <div key={ad.ad_id} className="ad-item">
                <a href="#">
                  {ad.photo ? (
                    <img src={`data:image/jpeg;base64,${ad.photo}`} alt={ad.title} className="ad-photo" />
                  ) : (
                    <div className="no-photo">No photo available</div>
                  )}
                  <div className="ad-title">{ad.title}</div>
                </a>
              </div>
            ))}
          </div>
        )
      ) : (
        <div>Connecting to server...</div>
      )}

    
    </div>
  );
};

export default MainPage;
