import React, { useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';

import Home from './components/Home';
import Register from './components/Register';
import Layout from './components/Layout';
import CreateAd from './components/CreateAd';
import { UserProvider } from './UserContext';
import MainPage from './components/MainPage'; // Исправлено на MainPage

const App = () => {
  const location = useLocation();

  useEffect(() => {
    document.body.className = ''; // Сброс классов
    if (location.pathname === '/') {
      document.body.classList.add('home-page');
    } else if (location.pathname === '/register') {
      document.body.classList.add('register-page');
    } else if (location.pathname.startsWith('/main')) {
      document.body.classList.add('main-page');
    }
  }, [location.pathname]);

  return (
    <UserProvider>
      <Routes>
        <Route exact path="/" element={<Home />} />
        <Route path="/register" element={<Register />} />
        <Route path="/main" element={<Layout />}>
          <Route index element={<MainPage />} /> {/* Главная страница внутри Layout */}
          <Route path="create-ad" element={<CreateAd />} />
        </Route>
      </Routes>
    </UserProvider>
  );
};

const Root = () => (
  <Router>
    <App />
  </Router>
);

export default Root;
